import React from 'react'
import classes from './CartPage.module.css'
import { useCart } from '../../../Hooks/useCart';
import Title from '../../Title/Title';

export default function CartPage() {
    const {cart, removeitem, changequant} =useCart();
  return (
    <>
        {cart && cart.items.length > 0 && (
            <div className={classes.container}>
                <ul className={classes.list}>
                    {cart.items.map(item => (
                    <li key={item.food.id}>
                        <div>
                            <img src={`/foods/${item.food.image}`}
                            alt = {item.food.name} />
                        </div>
                        <div>
                            {item.food.name}
                        </div>
                        <div>
                            <select value={item.quantity} onChange={e => changequant(item, Number(e.target.value))}>
                                <option>1</option>
                                <option>2</option>
                                <option>3</option>
                                <option>4</option>
                                <option>5</option>
                            </select>
                        </div>
                        <div>
                            Tk.{item.price}
                        </div>
                        <div>
                            <button className={classes.rembut} onClick ={() => removeitem(item.food.id)}>Remove Item</button>
                        </div>
                    </li>
                    ))}
                    
                </ul>
                <div>
                    Total Price: Tk.{cart.totalPrice}
                </div>
            </div>

        )}
    </>

  )
}
