import classes from './FoodAdmin.module.css';
import { useEffect, useState } from 'react';
import { getAll } from '../../../Services/foodService';
import { Link } from 'react-router-dom';

export default function FooodAdmin() {
  const [foods, setFoods] = useState();

  useEffect(() => {
    loadFoods();
  });

  const loadFoods = async () => {
    const foods = await getAll();
    setFoods(foods);
  };



  return (
    <div className={classes.container}>
      <div className={classes.list}>
        <Link to='/foodadd' className={classes.addfood}>
          Add
        </Link>
        {
          foods && foods.map (food => 
            <div key={food.id} className={classes.foodlist}>
              <img src={food.image} alt={food.name}/>
              <div>{food.name}</div>
              <div>Tk.{food.price}</div>
              <div className={classes.actions}>
              <div><Link to={'/editfood' + food.id}>Edit</Link></div>
              <div><Link>Delete</Link></div>
              </div>

            </div>)
        }
      </div>
    </div>
  )
}
