import React, { createContext, useContext, useEffect, useState } from 'react'

const CartContext = createContext(null)

export default function Provider({children}) {
    const [cartItems, setCartItems] = useState();
    const [totalPrice, setTotalPrice] = useState();
    const [totalCount, setTotalCount] = useState();

    useEffect(
        () => {
            const total = sum(cartItems.map())
        }, [cartItems]
    );
    
    const sum = items => {
        return items.reduce((prev, cur) => prev + cur, 0)
    }
    const removeitem = foodId => {
        const filteredcart = cartItems.filter(item => item.food.id !== foodId);
        setCartItems(filteredcart);
    }

    const changequant = (cartItem, newquant) => {
        const {food} = cartItems;

        const changes = {
            ...cartItems,
            quantity: newquant,
            price: food.price * newquant,
        };

        setCartItems (
            cartItems.map (item => (food.id ? changequant : item))
        );

        }

    return (
    <CartContext.Provider value={{cart: {items: cartItems, totalPrice, totalCount}, removeitem, changequant}}> 
        {children}
    </CartContext.Provider>
  )
}

export const useCart = () => useContext(CartContext);
